.OrderButton {
    background-color: #1268da;
    outline: none;
    cursor: pointer;
    border: none;
    border-radius: 5px;
    color: white;
    font-family: inherit;
    font-size: 1.2em;
    padding: 15px 30px;
    font-weight: bold;
    opacity: 0.7;
    transition: all 300ms ease-in-out;
}

.OrderButton:hover, .OrderButton:active {
    opacity: 1;

}

.OrderButton:disabled {
    background-color: #C7C6C6;
    cursor: not-allowed;
    border: 1px solid #ccc;
    color: #888888;
}

.OrderButton:not(:disabled) {
    animation: enable 0.3s linear;
}

@keyframes enable {
    0% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
    }
}
